&#39; #define _DEBUG_ 1 // debug toggle namespace Fast_IO { #ifndef _DEBUG_ #define gc() (iS == iT ? (iT = (iS = ibuff) + fread(ibuff, 1, SIZ, stdin), (iS == iT ? EOF : *iS++)) : *iS++) #else #define gc() getchar() #endif const int SIZ = 1 &lt;&lt; 21 | 1; char *iS, *iT, ibuff[SIZ], obuff[SIZ], *oS = obuff, *oT = oS + SIZ - 1, fu[110], c; int fr; inline void ioout() { fwrite(obuff, 1, oS - obuff, stdout); oS = obuff; } template &lt;class Type&gt; inline void read(Type&amp; x) { x = 0; Type y = 1; for (c = gc(); (c &gt; &#39;9&#39; || c &lt; &#39;0&#39;) &amp;&amp; c ^ &#39;-&#39;; c = gc()) ; c == &#39;-&#39; ? y = -1 : x = (c &amp; 15); for (c = gc(); c &gt;= &#39;0&#39; &amp;&amp; c &lt;= &#39;9&#39;; c = gc()) x = x * 10 + (c &amp; 15); x *= y; } inline bool blank(char ch) { return ch == &#39; &#39; || ch == &#39;
&#39; || ch == &#39;&#39; || ch == &#39;	&#39;; } inline void read(char* s) { register char ch = gc(); for (; blank(ch); ch = gc()) ; for (; !blank(ch); ch = gc()) *s++ = ch; *s = 0; } inline void read(char&amp; c) { for (c = gc(); blank(c); c = gc()) ; } template &lt;typename Type, typename... Args&gt; inline void read(Type&amp; t, Args&amp;... args) { read(t), read(args...); } template &lt;typename... Args&gt; inline void read(char* t, Args&amp;... args) { read(t), read(args...); } template &lt;typename... Args&gt; inline void read(char&amp; t, Args&amp;... args) { read(t), read(args...); } template &lt;class Type&gt; inline void write(char lastChar, Type x) { if (x &lt; 0) *oS++ = &#39;-&#39;, x = -x; if (x == 0) *oS++ = &#39;0&#39;; while (x) fu[++fr] = x % 10 + &#39;0&#39;, x /= 10; while (fr) *oS++ = fu[fr--]; *oS++ = lastChar; ioout(); } inline void write(char lastChar, char x[]) { for (register int i = 0; x[i]; ++i) *oS++ = x[i]; *oS++ = lastChar; ioout(); } inline void write(char lastChar, char x) { *oS++ = x; *oS++ = lastChar; ioout(); } template &lt;typename Type, typename... Args&gt; inline void write(char midChar, Type t, Args... args) { write(midChar, t), write(midChar, args...); } } // namespace Fast_IO using Fast_IO::read; using Fast_IO::write; namespace LinkCutTree { const int SIZE = 12e4 + 5; struct SPLAY { int ch[2]; int fa; int key; int maxValue; int lazyTag; } T[SIZE]; stack &lt; int &gt; MemoryWaste; #define ls T[x].ch[0] #define rs T[x].ch[1] #define WhichSon(x) (T[T[x].fa].ch[1] == x) #define IsRoot(x) (T[T[x].fa].ch[0] ^ x &amp;&amp; T[T[x].fa].ch[1] ^ x) void UpdateMessage(int x) { T[x].maxValue = max(max(T[ls].maxValue, T[x].key), T[rs].maxValue); } void UpdateSons(int x) { if (T[x].lazyTag) { ls ^= rs ^= ls ^= rs; T[x].lazyTag = 0; T[ls].lazyTag ^= 1; T[rs].lazyTag ^= 1; } } void RotateNode(int x) { int y = T[x].fa; if (!IsRoot(y)) T[T[y].fa].ch[WhichSon(y)] = x; bool k = WhichSon(x); T[x].fa = T[y].fa; T[y].fa = x; T[y].ch[k] = T[x].ch[k ^ 1]; T[T[y].ch[k]].fa = y; T[x].ch[k ^ 1] = y; UpdateMessage(y); UpdateMessage(x); } void LinkSplay(int x) { int u = x; while (!IsRoot(u)) MemoryWaste.push(u), u = T[u].fa; MemoryWaste.push(u); while (MemoryWaste.size()) UpdateSons(MemoryWaste.top()), MemoryWaste.pop(); for (; !IsRoot(x); RotateNode(x)) { int y = T[x].fa; if (!IsRoot(y)) RotateNode(WhichSon(x) ^ WhichSon(y) ? x : y); } } void AccessEdge(int x) { for (int u = x, y = 0; u; y = u, u = T[u].fa) { LinkSplay(u); T[u].ch[1] = y; UpdateMessage(u); } } void MakeRoot(int x) { AccessEdge(x); LinkSplay(x); T[x].lazyTag ^= 1; } void SplitTree(int x, int y) { MakeRoot(x); AccessEdge(y); LinkSplay(y); } void LinkTree(int x, int y) { MakeRoot(x); T[x].fa = y; } void CutTree(int x, int y) { MakeRoot(x); AccessEdge(y); LinkSplay(y); T[x].fa = T[y].ch[0] = 0; } int FindByKey(int x, int u) { if (T[x].key == u) return x; else if (T[ls].maxValue == u) return FindByKey(ls, u); else return FindByKey(rs, u); } } // namespace LinkCutTree using namespace LinkCutTree; int F[1005][1005], U[101000], V[101000]; int OP[101000], ans[101000], n, m, QueryNumber; struct EdgeNode { int x, y; int val, key; EdgeNode() { key = 1; } friend bool operator &lt; (EdgeNode X, EdgeNode Y) { return X.val &lt; Y.val; } } e[101000]; struct UnionFindSet { int fa[1010]; int find(int x) { if (x ^ fa[x]) fa[x] = find(fa[x]); return fa[x]; } void merge(int x, int y) { int u = find(x), v = find(y); if (u ^ v) fa[u] = v; } void init(int n, int m) { for (int i = 1; i &lt;= n; ++i) fa[i] = i; for (int i = 1; i &lt;= m; ++i) { if (e[i].key &amp;&amp; find(e[i].x) ^ find(e[i].y)) { merge(e[i].x, e[i].y); LinkTree(e[i].x, n + i); LinkTree(e[i].y, n + i); } } } } ufs; signed main() { read(n, m, QueryNumber); for (int i = 1; i &lt;= m; ++i) read(e[i].x, e[i].y, e[i].val); sort(e + 1, e + 1 + m); for (int i = 1; i &lt;= m; ++i) { F[e[i].x][e[i].y] = i; F[e[i].y][e[i].x] = i; T[n + i].key = e[i].val; } for (int i = 1; i &lt;= QueryNumber; ++i) { read(OP[i], U[i], V[i]); if (OP[i] == 2) e[F[U[i]][V[i]]].key = 0; } ufs.init(n, m); int EdgeCount = 0; for (int i = QueryNumber; i &gt;= 1; --i) { SplitTree(U[i], V[i]); if (OP[i] == 1) ans[++EdgeCount] = T[V[i]].maxValue; else { int Temporary = FindByKey(V[i], T[V[i]].maxValue); if (T[F[U[i]][V[i]] + n].key &lt; T[V[i]].maxValue) { CutTree(e[Temporary - n].x, Temporary); CutTree(e[Temporary - n].y, Temporary); LinkTree(U[i], F[U[i]][V[i]] + n); LinkTree(V[i], F[U[i]][V[i]] + n); } } } for (int i = EdgeCount; i &gt;= 1; --i) write(io_l, ans[i]); return 0; } 22.P3302 [SDOI2013]æ£®æ—å°Zæœ‰ä¸€ç‰‡æ£®æ—ï¼Œå«æœ‰Nä¸ªèŠ‚ç‚¹ï¼Œæ¯ä¸ªèŠ‚ç‚¹ä¸Šéƒ½æœ‰ä¸€ä¸ªéè´Ÿæ•´æ•°ä½œä¸ºæƒå€¼ã€‚åˆå§‹çš„æ—¶å€™ï¼Œæ£®æ—ä¸­æœ‰Mæ¡è¾¹ã€‚ å°Zå¸Œæœ›æ‰§è¡ŒTä¸ªæ“ä½œï¼Œæ“ä½œæœ‰ä¸¤ç±»ï¼š Q x y kæŸ¥è¯¢ç‚¹xåˆ°ç‚¹yè·¯å¾„ä¸Šæ‰€æœ‰çš„æƒå€¼ä¸­ï¼Œç¬¬kå°çš„æƒå€¼æ˜¯å¤šå°‘ã€‚æ­¤æ“ä½œä¿è¯ç‚¹xå’Œç‚¹yè¿é€šï¼ŒåŒæ—¶è¿™ä¸¤ä¸ªèŠ‚ç‚¹çš„è·¯å¾„ä¸Šè‡³å°‘æœ‰kä¸ªç‚¹ã€‚ L x yåœ¨ç‚¹xå’Œç‚¹yä¹‹é—´è¿æ¥ä¸€æ¡è¾¹ã€‚ä¿è¯å®Œæˆæ­¤æ“ä½œåï¼Œä»ç„¶æ˜¯ä¸€ç‰‡æ£®æ—ã€‚ ä¸ºäº†ä½“ç°ç¨‹åºçš„åœ¨çº¿æ€§ï¼Œæˆ‘ä»¬æŠŠè¾“å…¥æ•°æ®è¿›è¡Œäº†åŠ å¯†ã€‚è®¾lastansä¸ºç¨‹åºä¸Šä¸€æ¬¡è¾“å‡ºçš„ç»“æœï¼Œåˆå§‹çš„æ—¶å€™lastansä¸º0ã€‚ å¯¹äºä¸€ä¸ªè¾“å…¥çš„æ“ä½œQ x y k,å…¶çœŸå®æ“ä½œä¸ºQ x^lastans y^lastans k^lastansã€‚ å¯¹äºä¸€ä¸ªè¾“å…¥çš„æ“ä½œL x yï¼Œå…¶çœŸå®æ“ä½œä¸ºL x^lastans y^lastansã€‚å…¶ä¸­^è¿ç®—ç¬¦è¡¨ç¤ºå¼‚æˆ–ï¼Œç­‰ä»·äºpascalä¸­çš„xorè¿ç®—ç¬¦ã€‚ è¯·å†™ä¸€ä¸ªç¨‹åºä¾†å¸®åŠ©å°Zå®Œæˆè¿™äº›æ“ä½œã€‚ æŸ¥è¯¢æ“ä½œæ˜¾ç„¶å¯ä»¥ç”¨ä¸»å¸­æ ‘æ¥å®Œæˆï¼Œç„¶è€Œè¿æ¥æ ‘çš„æ“ä½œåˆè®©æˆ‘ä»¬æƒ³åˆ°äº†lctã€‚æ€ä¹ˆåŠå‘¢ï¼Ÿä¸»å¸­æ ‘+LCTï¼Ÿ(æ®è¯´è¿˜çœŸæœ‰äººè¿™ä¹ˆå¹²) å¯å‘å¼åˆå¹¶ï¼ æ²¡é”™ï¼Œæˆ‘ä»¬ç”¨é€‰æ‹©ç”¨ä¸»å¸­æ ‘æ¥å®Œæˆè¿™é“é¢˜ï¼Œåˆå¹¶æ—¶é‡‡ç”¨å¯å‘å¼åˆå¹¶ã€‚ å…·ä½“æ¥è¯´å°±æ˜¯æ¯æ¬¡åˆå¹¶æ—¶éƒ½ç”¨å¤§å°è¾ƒå°çš„æ ‘å¾€å¤§çš„åˆå¹¶ï¼Œç„¶åæš´åŠ›éå†å¤§å°è¾ƒå°çš„æ ‘æ›´æ–°å€å¢æ•°ç»„å’Œä¸»å¸­æ ‘å³å¯ #pragma GCC diagnostic error &quot;-std=c++11&quot; #include &lt;cstdio&gt; #include &lt;iostream&gt; #include &lt;algorithm&gt; #include &lt;cstring&gt; #include &lt;queue&gt; #define _size_ (tr[tr[x].l].size + tr[tr[y